function App() {
  const [boxes, mutate] = useStore(
    (state) => [state.boxes, state.mutate],
    shallow
  );

  useEffect(() => {
    function animate() {
      mutate();
      requestAnimationFrame(animate);
    }
    animate();
  }, [mutate]);

  return (
    <Canvas>
      {boxes.map((id) => (
        <Box key={id} id={id} />
      ))}
      <Stars />
    </Canvas>
  );
}
